import {Link} from 'react-router-dom'
import axios from 'axios'
import { createUrl, log } from '../utils/utils'
import { useEffect, useState } from "react";

function MyOrders() {

 const [orders,setOrders] = useState([]);


 const counter = 1;
 const incrementCounter = () => {
   counter++;
 };

 const [status,setStatus] = useState(true);
 const renderOrderStatus = (orderStatus) => {
  if (orderStatus) {
    return "on the wayy";
  } else {
    return "ordered";
  }
};

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    try {
      const token = sessionStorage.getItem("jwt");
      console.log("hello : "+token);
      const userId = sessionStorage.getItem("id");
      const url = createUrl(`/order/get/${userId}`);

      console.log("token : "+token);
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      const response = await axios.get(url,config);
      setOrders(response.data);
      log(response.data);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };


  return (
    <div>
<center>
      <h2>My Orders</h2></center>
  <hr/>
      <div>

      <table class="table table-borderless">
  <thead>
    <tr>
      <th scope="col">Order No</th>
      <th scope="col">Order Date</th>
      <th scope="col">Order Status</th>
      <th scope="col">Order Total</th>
    </tr>
  </thead>
  <tbody style={{border:"2px solid gray", borderRadius:"2px"}}>
  {orders.map((a,incrementCounter)=>( 
    <tr>
      <td>{incrementCounter}</td>
      <td>{a.order_date.substring(0,10)}<br/>
      <Link to="/orderdetails" style={{color:'green'}}>view order </Link> 
      </td>
      <td>{renderOrderStatus(a.order_status)}</td>
      <td>â‚¹ {a.order_total.toFixed(2)}</td>
    </tr>
  ))}
</tbody>
</table>
    </div>
    </div>
  )
}

export default MyOrders
