import { useEffect, useState } from 'react'
import { toast } from 'react-toastify'
import { Link,useNavigate } from 'react-router-dom';

function AdminDashboard(){
    const navigate = useNavigate()
    const [products, setProducts] = useState([])
  
    useEffect(() => {
      loadProducts();
    }, [])
  
    const loadProducts = async () => {
      const response = await getProductList()
      console.log(response)  
      setProducts(response)
    }

  
    return (
      <>
      <div style={{position:"relative",minHeight:"100%" ,margin:"0"}}>
    <div className=''>
      <h1 style={{ textAlign: 'center', margin: 10 }}>Product Gallery</h1>
      <div className='row' style={{ marginTop: 50}}>
        {products.map((product) => {
          return (
            <div className='col-md-3'>
              <div className='card'>
             
                <div className='card-body'>
                  <h5 className='card-title'>
                    <Link to={`/item/${product.id}`}>
                    <img  src={`http://localhost:8000/images/${product.productImage}`} alt="productimage" style={{ height: 200,width:220 }}/>
                      <div>{product['company']}</div>
                      <div>{product['productName']}</div>
                    </Link>
                   <h6>{product['category']}</h6> 
                    </h5>
                  <div className='card-text'>
                    {/* <div>{product['company']}</div>  */}
                     <div>â‚¹ {product['price']}</div>                   
                  </div>
                </div>
              </div><br/>
            </div>
          )
         })} 
      </div>
      </div>
    </div>
      </>
    );
}


export default AdminDashboard

