import { useEffect, useState } from 'react'
import { toast } from 'react-toastify'
import { Link,useNavigate } from 'react-router-dom';
import AdminLogin from './AdminLogin';
import Adminorders from './Adminorders';
import Adminproducts from './Adminproducts';

function AdminDashboard(){
    const navigate = useNavigate()
    const [products, setProducts] = useState([])
    const [navadmin, setnavAdmin] = useState()
  

    const handleProductsClick = () => {
        setActiveComponent('products');
      };
    
      const handleOrdersClick = () => {
        setActiveComponent('orders');
      };
  
    // useEffect(() => {
    //   loadProducts();
    // }, [])
  
    const loadProducts = async () => {

    }
  
    return (
        <div style={{display:""}}>
        <div>
        <div class="container-fluid" style={{paddingLeft:"0"}}>
      <div class="row flex-nowrap">
          <div class="col-auto col-md-3 col-xl-2 px-sm-2 px-0 bg-dark">
              <div class="d-flex flex-column align-items-center align-items-sm-start px-3 pt-2 text-white min-vh-100">
                  <a href="/" class="d-flex align-items-center pb-3 mb-md-0 me-md-auto text-white text-decoration-none">
                      <span class="fs-5 d-none d-sm-inline">Quick Links</span>
                  </a>
                  <ul class="nav nav-pills flex-column mb-sm-auto mb-0 align-items-center align-items-sm-start" id="menu">
                      <li class="nav-item">
                          <a href="#" class="nav-link align-middle px-0">
                              <i class="fs-4 bi-house"></i> <span class="ms-1 d-none d-sm-inline">Dashboard</span>
                          </a>
                      </li>
                      <li>
                          <a href="#submenu1" data-bs-toggle="collapse" class="nav-link px-0 align-middle">
                              <i class="fs-4 bi-speedometer2"></i> <span class="ms-1 d-none d-sm-inline">Summary</span> </a>
                          <ul class="collapse show nav flex-column ms-1" id="submenu1" data-bs-parent="#menu">
                              <li class="w-100">
                              <button className='nav-link px-0' to='' onClick={onChangeNav("products")}>Products</button>
                              </li>
                              <li>
                              <Link className='nav-link px-0' to='/adminusers'>Users</Link>
                              </li>
                              <li>
                      {/* <button className='nav-link px-0' to='' onClick={()=>{
                      document.getElementById('content')
                      .innerText=<Adminorders/>}}>Orders</button>  */}
                       <div>
      <button className='nav-link px-0' onClick={handleProductsClick}>Products</button>
      <button className='nav-link px-0' onClick={handleOrdersClick}>Orders</button>
      {activeComponent === 'products' && <Adminproducts />}
      {activeComponent === 'orders' && <Adminorders />}
                   </div>
                      </li>
                          </ul>
                      </li>
                      
                  </ul>
                  <hr/>
                  {/* <div class="dropdown pb-4">
                      <a href="#" class="d-flex align-items-center text-white text-decoration-none dropdown-toggle" id="dropdownUser1" data-bs-toggle="dropdown" aria-expanded="false">
                          <img src="https://github.com/mdo.png" alt="hugenerd" width="30" height="30" class="rounded-circle"/>
                          <span class="d-none d-sm-inline mx-1">loser</span>
                      </a>
                      <ul class="dropdown-menu dropdown-menu-dark text-small shadow">
                          <li><a class="dropdown-item" href="#">New project...</a></li>
                          <li><a class="dropdown-item" href="#">Settings</a></li>
                          <li><a class="dropdown-item" href="#">Profile</a></li>
                          <li>
                              <Link class="dropdown-divider"/>
                          </li>
                          <li><a class="dropdown-item" href="#">Sign out</a></li>
                      </ul>
                  </div> */}
              </div>
          </div>
          <div class="col py-3" style={{paddingLeft:"0",paddingTop:"0", border:"1px solid black"}}>
          <div>
        <nav className='navbar navbar-expand-lg bg-body-tertiary' style={{border:"1px solid black",marginTop:"0"}}>
          <div className='container-fluid'>
            <a className='navbar-brand'>E-Shopping Admin Panel</a>
            <button
              className='navbar-toggler'
              type='button'
              data-bs-toggle='collapse'
              data-bs-target='#navbarSupportedContent'
              aria-controls='navbarSupportedContent'
              aria-expanded='false'
              aria-label='Toggle navigation'>
              <span className='navbar-toggler-icon'></span>
            </button>
            <div className='d-flex'>
          <button class="btn btn-secondary" type="button" >Log out</button>      
            </div>
          </div>
        </nav>
        </div>
        <div id='content' style={{height:"90%",width:"100%", border:"2px solid red"}}>
         {navadmin}
        </div>
  
          </div>
      </div>
  </div>
  
        </div>
       
      </div>
    );
}


export default AdminDashboard

