import { Link } from "react-router-dom";
import profileIcon from "./images/img2.jpeg";
import { useState,useEffect } from "react";

function Profile() {

  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const imageDimensions = {
    width: "70px",
    height: "70px",
    borderRadius: "70px",
  };
  const first = sessionStorage.getItem('first_name')
  const last = sessionStorage.getItem('last_name')
    const emaill = sessionStorage.getItem('email')
    const mob = sessionStorage.getItem('mob_no')
    const gender = sessionStorage.getItem('gender')

    const handleSectionClick = (section) => {
      setDisplaySection(section);
    };

    const [displaySection, setDisplaySection] = useState("profile");
    const showProfile = displaySection === "profile";
    const showUpdate = displaySection === "update";
    const showPassword = displaySection === "password";
  

  return (
    <>
      <center>
        <h2>Manage your Profile</h2>
      </center>
<div style={{ justifyContent: 'center' }}>
      <div className="card text">
  <div className="card-header">
    <ul className="nav nav-pills card-header-pills">
      <li className="nav-item">
      <button
                  className={`nav-link ${showProfile ? "active" : ""}`}
                  onClick={() => handleSectionClick("profile")}
                >
                  Profile
                </button>
        {/* <Link className="nav-link active" to="#"
          onClick={() => {
            document.getElementById("profileCard").hidden = false;
            document.getElementById("passwordCard").hidden = true;
            document.getElementById("updateCard").hidden = true;
          }}
        >Profile</Link> */}
      </li>
      <li className="nav-item">
      <button
                  className={`nav-link ${showUpdate ? "active" : ""}`}
                  onClick={() => handleSectionClick("update")}
                >
                  Update Profile
                </button>
        {/* <Link className="nav-link" to="#"
         onClick={() => {
          document.getElementById("updateCard").hidden = false;
          document.getElementById("profileCard").hidden = true;
          document.getElementById("passwordCard").hidden = true;
        }}
        >Update Profile</Link> */}
      </li>
      <li className="nav-item">
      <button
                  className={`nav-link ${showPassword ? "active" : ""}`}
                  onClick={() => handleSectionClick("password")}
                >
                  Change Password
                </button>
        {/* <Link className="nav-link" to="#"
         onClick={() => {
          document.getElementById("profileCard").hidden = true;
          document.getElementById("passwordCard").hidden = false;
          document.getElementById("updateCard").hidden = true;
        }}
        >Change Password</Link> */}
      </li>
    </ul>
  </div>
  {/* ------------------------------------------------------------------------- */}
  {showProfile && (<div id="profileCard" style={{margin:"20px"}}>
  <div  className="card-body">
    <h6 className="card-title">{first} {last}</h6>
    <p className="card-text">Account Holder</p>
  </div>
  <div className="card-body">
    <h6 className="card-title">{gender}</h6>
    <p className="card-text">Gender</p>
  </div>
  <div className="card-body">
    <h6 className="card-title">{emaill}</h6>
    <p className="card-text">Email Id</p>
  </div>
  <div className="card-body">
    <h6 className="card-title">Contact No</h6>
    <p className="card-text">{mob}</p>
  </div>
  </div>)}
  {/* ------------------------------------------------------------------------- */}

  {/* ------------------------------------------------------------------------- */}
  {showUpdate && (<div id="passwordCard" className="col-12 d-flex justify-content" style={{margin:"20px"}}>
      <div className="form">
        <form >
          <div className="mb-3">
            <label htmlFor="currentPassword">Current Password</label>
            <input
              type="password"
              className="form-control"
              value={currentPassword}
              onChange={(e) => setCurrentPassword(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="newPassword">New Password</label>
            <input
              type="password"
              className="form-control"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              required
            />
          </div>

          <div className="mb-3">
            <label htmlFor="confirmPassword">Confirm Password</label>
            <input
              type="password"
              className="form-control"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
          </div>

          <div style={{ display: 'flex', justifyContent: 'center' }}>
            <div className="mb-3">
              <button type="submit" className="btn btn-success">
                Change Password
              </button>
            </div>
          </div>
        </form>
      </div>
    </div>)}
  {/* ------------------------------------------------------------------------- */}
  
  {/* ------------------------------------------------------------------------- */}
  ({showPassword && (<div id="updateCard" style={{position:"relative",minHeight:"100%" ,margin:"0"}}>
      <center><h2>Manage your Profile</h2></center>
      <div style={{display:'flex'}}>
      <img src={profileIcon} alt="Profile" style={imageDimensions}/>&nbsp;&nbsp;&nbsp;<h3 style={{paddingTop:'10px'}}>{first}<h6>{emaill}</h6></h3>
      
      </div>

      <div>
        <div className='col'></div>
        <div className='col'>
          <div className='form'>
            <div className='mb-3'>
              <label htmlFor=''>First Name</label>
              <input
                type='text'
                className='form-control'
                onChange={(e) => {
                //   setFirstName(e.target.value)
                }}
              />
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Last Name</label>
              <input
                type='text'
                className='form-control'
                onChange={(e) => {
                //   setLastName(e.target.value)
                }}
              />
