// import { Delete, Edit} from "@mui/icons-material";
// import { Button  } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react"
import { Link } from "react-router-dom";
// import { createUrl, log } from "../utils/utils";
import { createUrl,log } from "../../utils/utils";
// import { create } from "@mui/material/styles/createTransitions";
import { toast } from "react-toastify";

function Adminusers(){
    const [users,setUsers] = useState([])
    const [firstName, setFirstName] = useState('')
    const [lastName, setLastName] = useState('')
    const [email, setEmail] = useState('')
    const [gender, setGender] = useState('')
    const [mobile, setMobile] = useState('')
    const [password, setPassword] = useState('')
    const [confirmPassword, setConfirmPassword] = useState('')

    useEffect( ()=>{
         loadAllData();
    },[])
    
    const loadAllData=async ()=>{
        try{
            const url = createUrl(`/user/get`)
            const token = sessionStorage.getItem("jwt");
            const config = {
                headers:{Authorization:`Bearer ${token}`}
              };
    
            const response =await axios.get(url,config)
            setUsers(response.data)
            log(response.data)
            console.log(response)
            }catch(error){
                console.error('Error fetching data: ',error);
            }
    }

    const addUser = async  ()=>{
      if (firstName.length == '') {
        toast.error('Please enter first name')
      } else if (lastName.length == '') {
        toast.error('Please enter last name')
      } else if (email.length == '') {
        toast.error('Please enter email')
      } else if (mobile.length == '' ) {
        toast.error('Please enter mobile')
      } else if (password.length == '') {
        toast.error('Please enter password')
      } else if (confirmPassword.length == '') {
        toast.error('Please confirm password')
      } else if (password !== confirmPassword) {
        toast.error('Password does not match')
      }
        const url = createUrl(`/auth/register`)
    
        const body = {
          first_name:firstName,last_name: lastName, email, password, gender, mob_no:mobile
           };
      
        try{
            const response = await axios.post(url,body);
            // loadAllData();
            // document.getElementById("updateUser").hidden=true
            // document.getElementById("userTable").hidden=false
            // log(response);
            setUsers(response);
            toast.success(`User Added Successfully`);
            return response.data;
           
        }catch(error){
            console.error('Error fetching data: ',error);
            return null;
        }
    }

    const[fuser,setfuser] = useState([]);
    const editfetchUser =async (e) => {
      const url = createUrl(`/user/getUser/${e}`)
        try{
          const token = sessionStorage.getItem("jwt");
          console.log("jwt: "+token);
          const config = {
            headers:{Authorization:`Bearer ${token}`}
          };
          const response = await axios.get(url,config);
          log(response.data);
          setfuser(response.data);

          // toast.success(`User Successfully updated`);
          // loadAllData();
        
      }catch(error){
        console.error('Error fetching data: ',error);
        return null;
      }}

      const editUser =async (e) => {
        const url = createUrl(`/user/update?uuid=${e}`)
          try{
            const token = sessionStorage.getItem("jwt");
            console.log("jwt: "+token);
            const config = {
              headers:{Authorization:`Bearer ${token}`}
            };
            const body = {
              first_name:firstName,last_name: lastName, email, password, gender, mob_no:mobile
               };
            const response = await axios.put(url,body,config);
            toast.success(`User Successfully updated`);
            loadAllData();
          
        }catch(error){
          console.error('Error fetching data: ',error);
          return null;
        }}


    const deleteUser =async (e) => {
      const url = createUrl(`/user/delete?uuid=${e}`)
        try{
          const token = sessionStorage.getItem("jwt");
          console.log("jwt: "+token);
          const config = {
            headers:{Authorization:`Bearer ${token}`}
          };
          const response = await axios.delete(url,config);
          toast.success(`User Successfully deleted`);
          loadAllData();
        
      }catch(error){
        console.error('Error fetching data: ',error);
        return null;
      }}

  return(
<>
<center>
{/* <h1>
    Admin products Page
</h1> */}

<div id="updateUser" hidden="true" style={{justifyContent:"center", textAlign:"center"}}>
          <h1 style={{ textAlign: 'center', margin: 10 }}>Add User</h1>
          <div className='row'>
            <div className='col-12 d-flex justify-content-center'>
              <div className='form'>
                <div className='mb-3'>
                  <label htmlFor=''>First Name</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setFirstName(e.target.value)
                    }}
                    
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Last Name</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setLastName(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Email</label>
                  <input
                    type='email'
                    className='form-control'
                    onChange={(e) => {
                      setEmail(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3'>
              <label htmlFor=''>Gender</label>&nbsp;&nbsp;<br/>
              <input type="radio" value="Male" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Male&nbsp;&nbsp;
              <input type="radio" value="Female" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Female&nbsp;&nbsp;
              <input type="radio" value="Other" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Other
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Mobile Number</label>
              <input
                type='tel'
                className='form-control'
                onChange={(e) => {
                  setMobile(e.target.value)
                }}
              />
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Password</label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => {
                  setPassword(e.target.value)
                }}
              />
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Confirm Password</label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => {
                  setConfirmPassword(e.target.value)
                }}
              />
            </div>

<div style={{display:"flex"}}>
<div className='mb-3'>
                  <button 
                    onClick={()=>{document.getElementById("updateUser").hidden=true
                    document.getElementById("userTable").hidden=false
                }}
                  className='btn btn-success'>
                   Back
                  </button>
                </div>&nbsp;

<div className='mb-3'>
                  <button onClick={()=>{addUser();}} className='btn btn-success'>
                    Add User
                  </button>
                </div>
                </div>
              </div>
            </div>
            <div className='col'></div>
            </div>
        </div>



        <div id="editUser" hidden="true" style={{justifyContent:"center", textAlign:"center"}}>
          <h1 style={{ textAlign: 'center', margin: 10 }}>Edit User</h1>
          <div className='row'>
            <div className='col-12 d-flex justify-content-center'>
              <div className='form'>
                <div className='mb-3'>
                  <label htmlFor=''>First Name</label>
                  <input
                    type='text'
                    value={fuser.first_name}
                    className='form-control'
                    onChange={(e) => {
                      setFirstName(e.target.value)
                    }}
                    
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Last Name</label>
                  <input
                    type='text'
                    value={fuser.last_name}
                    className='form-control'
                    onChange={(e) => {
                      setLastName(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Email</label>
                  <input
                    type='email'
                    value={fuser.email}
                    className='form-control'
                    onChange={(e) => {
                      setEmail(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3' >
              <label htmlFor=''>Gender</label>&nbsp;&nbsp;<br/>
              <input type="radio"  value="Male" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Male&nbsp;&nbsp;
              <input type="radio" value="Female" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Female&nbsp;&nbsp;
              <input type="radio" value="Other" name="gender" onChange={(e) => {setGender(e.target.value)}}/> Other
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Mobile Number</label>
              <input
                type='number'
                value={fuser.mobno}
                className='form-control'
                onChange={(e) => {
                  setMobile(e.target.value)
                }}
              />
            </div>

            <div className='mb-3'>
              <label htmlFor=''>Password</label>
              <input
                type='password'
                value={fuser.password}
                className='form-control'
                onChange={(e) => {
                  setPassword(e.target.value)
                }}
              />
            </div>

<div style={{display:"flex"}}>
<div className='mb-3'>
                  <button 
                    onClick={()=>{document.getElementById("updateUser").hidden=true
                    document.getElementById("userTable").hidden=false
                    document.getElementById("editUser").hidden=true
                }}
                  className='btn btn-success'>
                   Back
                  </button>
                </div>&nbsp;

<div className='mb-3'>
                  <button value={fuser.id} onClick={(w)=>{editUser(w.target.value);}} className='btn btn-success'>
                    Edit User
                  </button>
                </div>
                </div>
              </div>
            </div>
            <div className='col'></div>
            </div>
        </div>






              <div id="userTable">
              <div> 
              <button type="button" class="btn btn-warning" style={{margin:"10px"}}
                onClick={()=>{document.getElementById("updateUser").hidden=false
                document.getElementById("userTable").hidden=true
            }}
              >Add Users</button>
              </div>

          <table className="table table-hover">
          <thead>
              <tr>
              <th scope="col">id</th>
              <th scope="col" style={{width:"200px"}} >Name</th>
              <th scope="col">email</th>
              <th scope="col">Mobile No</th>
              <th scope="col">View</th>
              <th scope="col">Edit</th>
              <th scope="col">Delete</th> 

              </tr>
          </thead>
          <tbody>

              { users.map(e=>
              <tr>
              <th scope="row">{e.id}</th>
              <td>{e.first_name}</td>
              <td>{e.email}</td>
              <td>{e.mobno}</td>
              {/* <td>{e.company}</td> */}
              {/* <td>{e.category}</td> */}
              <td><button variant='contained' className="btn btn-primary" color="primary" >View</button></td>
              <td><button variant='contained' className="btn btn-info" color="primary" value={e.id} onClick= {() => {editfetchUser(e.id);
              document.getElementById("editUser").hidden=false;
              document.getElementById("userTable").hidden=true;}
            }
              
              >Edit</button></td>
              <td><button variant='contained' className="btn btn-danger" value={e.id} 
              onClick= {() => deleteUser(e.id)}
              color="error" >Delete</button></td>
              </tr>)}
          </tbody>
          </table>
          </div>
          </center>
</>
    );
}

export default Adminusers;
