import axios from 'axios'
import { createUrl, log } from '../utils/utils'
import { useState } from "react";
import { toast } from 'react-toastify'

export function Myaddress(){
    const [city, setCity] = useState('')
    const [country, setCountry] = useState('')
    const [houseNo, setHouseno] = useState('')
    const [landmark, setLandmark] = useState('')
    const [pincode, setPincode] = useState('')

    const registerAddress = async () => {
        if (city.length == '') {
          toast.error('Please enter City')
        } else if (country.length == '') {
          toast.error('Please enter Country')
        } else if (houseNo.length == '') {
          toast.error('Please enter House No')
        } else if (landmark.length == '' ) {
          toast.error('Please enter Landmark')
        } else if (pincode.length == '') {
          toast.error('Please enter Pincode')
        } else {

            const url = createUrl('/admin/products/all')
            
            const body = {city,country,houseNo,landmark,pincode};
            
            const token = sessionStorage.getItem("jwt");
            const config = {
                headers:{Authorization:`Bearer ${token}`}
              };

          try{
            const response = await axios.post(url, body,config);
            toast.success('Successfully registered a new user')
            navigate('/');
        }catch(error){
            console.error('Error fetching data: ',error);
            toast.error('Error while registering a new user, please try again')
            return null;
        } 
          }
        }

    return (
        <div>
          <h1 style={{ textAlign: 'center', margin: 10 }}>Register User</h1>
    
          <div className='row'>
            <div className='col'></div>
            <div className='col'>
              <div className='form'>
                <div className='mb-3'>
                  <label htmlFor=''>City</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setCity(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Country</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setCountry(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>House No</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setHouseno(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3'>
                  <label htmlFor=''>Landmark</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setLandmark(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Pin Code</label>
                  <input
                    type='number'
                    className='form-control'
                    onChange={(e) => {
                      setPincode(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <button onClick={registerAddress} className='btn btn-success'>
                    Register Address
                  </button>
                </div>
              </div>
            </div>
            <div className='col'></div>
          </div>
        </div>
      );
}
