import { Delete, Edit, RemoveRedEye} from "@mui/icons-material";
import { Button  } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react"
import { Link } from "react-router-dom";
// import { createUrl, log } from "../utils/utils";
import { createUrl,log } from "../../utils/utils";
// import { create } from "@mui/material/styles/createTransitions";
import { toast } from "react-toastify";
import {Modal,ModalBody,ModalFooter} from 'reactstrap';
// import '../../../node_modules/bootstrap/dist/js/bootstrap'
// import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css'



function Adminproducts(){
    const [products,setProducts] = useState([])
    const [productName,setproductName] = useState([]) 
    const [productPrice, setproductPrice] = useState([])
    const [productUnitstock, setproductUnitstock] = useState([])
    const [productDescription, setproductDescription] = useState([])
    const [productCompany, setproductCompany] = useState([])
    const [productCategory, setproductCategory] = useState([])
    const [productImage, setProductImage] = useState(null);
    const [modal,setModal] = useState(false);

    const toggleModal = () =>{
      setModal(!modal);
    };
  

    
    var popoverTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="popover"]'))
    var popoverList = popoverTriggerList.map(function (popoverTriggerEl) {
      return new bootstrap.Popover(popoverTriggerEl)
    })
    useEffect(()=>{
      loadAllData();
  },[])
  
  const loadAllData=async ()=>{
    try{
        const url = createUrl(`/admin/products/all`)
        const token = sessionStorage.getItem("jwt");
        const config = {
            headers:{Authorization:`Bearer ${token}`}
          };

        const response =await axios.get(url,config)
        setProducts(response.data)
        }catch(error){
            console.error('Error fetching data: ',error);
        }
}


const addProduct = async  ()=>{
    if (productName.length == '') {
      toast.error('Please enter Product name')
    } else  if (productPrice.length == '') {
      toast.error('Please enter Product Price')
    } else  if (productUnitstock.length == '') {
        toast.error('Please enter Product Unitstock')
    }else  if (productDescription.length == '') {
      toast.error('Please enter Product Description')
    } else if (productCompany.length == '') {
      toast.error('Please enter Product Company')
    } else if (productCategory.length == '') {
        toast.error('Please enter Product Category')
      } 
    const url = createUrl(`/admin/products`)
    const token = sessionStorage.getItem("jwt");

    const body = {
        productName, productPrice, productUnitstock ,productDescription, productCompany,productCategory
       };

       const formData = new FormData();
       formData.append('image', productImage); // Replace 'file' with the name you want to use for the file field
       formData.append('data', JSON.stringify(body)); // Replace 'jsonData' with the name you want to use for the JSON data field

       const config = {
        headers: {
          Authorization: `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      };

    try{
        const response = await axios.post(url, formData,config);
        // update();
        loadAllData();
        document.getElementById("updateVenue").hidden=true
        document.getElementById("productTable").hidden=false
        log(response.data);
        return response.data
       // setUsers1(response.data)
    }catch(error){
        console.error('Error fetching data: ',error);
        return null;
    }
}

const update = ()=>{
    document.getElementById("updateVenue").hidden=true
}

const deleteProduct = (e) => {
    try{
      const response = axios.delete('http://localhost:8000/admin/deletevenue/${e}')
    
  }catch(error){
    console.error('Error fetching data: ',error);
    return null;
  }
}



    return(
<>
<center>
{/* <h1>
    Admin products Page
</h1> */}

<div id="updateProduct" hidden="true" style={{justifyContent:"center", textAlign:"center"}}>
          <h1 style={{ textAlign: 'center', margin: 10 }}>Add Product</h1>
    
          <div className='row' >
           
            <div className='col-12 d-flex justify-content-center'>
              <div className='form'>
                <div className='mb-3'>
                  <label htmlFor=''>Product Name</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setproductName(e.target.value)
                    }}
                    
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Price</label>
                  <input
                    type='number'
                    className='form-control'
                    onChange={(e) => {
                      setproductPrice(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Unit Stock</label>
                  <input
                    type='number'
                    className='form-control'
                    onChange={(e) => {
                      setproductUnitstock(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3'>
                  <label htmlFor=''>Product Description</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setproductDescription(e.target.value)
                    }}
                  />
                </div>
    
                <div className='mb-3'>
                  <label htmlFor=''>Company</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setproductCompany(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3'>
                  <label htmlFor=''>Category</label>
                  <input
                    type='text'
                    className='form-control'
                    onChange={(e) => {
                      setproductCategory(e.target.value)
                    }}
                  />
                </div>

                <div className='mb-3'>
  <label htmlFor=''>Product Image</label>
  <input
    type='file'
    className='form-control'
    onChange={(e) => {
      setProductImage(e.target.files[0]);
    }}
  />
</div>
<div style={{display:"flex"}}>
<div className='mb-3'>
                  <button onClick={()=>{addProduct();}} className='btn btn-success'>
                   Back
                  </button>
                </div>&nbsp;

<div className='mb-3'>
                  <button onClick={()=>{addProduct();}} className='btn btn-success'>
                    Add product
                  </button>
                </div>
                </div>
              </div>
            </div>
            <div className='col'></div>
            </div>
        </div>
              <div id="productTable">
              <div> 
              <button type="button" className="btn btn-warning" style={{margin:"10px"}}
                onClick={()=>{document.getElementById("updateProduct").hidden=false
                document.getElementById("productTable").hidden=true
            }}
              >Add Products</button>
              </div>

          <table className="table table-hover">
          <thead>
              <tr>
              <th scope="col">id</th>
              <th scope="col" style={{width:"200px"}} >Product Name</th>
              <th scope="col">Price</th>
              <th scope="col">Unit Stock</th>
              {/* <th scope="col">Company</th> */}
              <th scope="col">Category</th>
              <th scope="col">View</th>
              <th scope="col">Edit</th>
              <th scope="col">Delete</th> 
              </tr>
          </thead>
          <tbody>

              {
                  products.map(e=>
              <tr>
              <th scope="row">{e.id}</th>
              <td>{e.productName}</td>
              <td>{e.price}</td>
              <td>{e.unitStock}</td>
              {/* <td>{e.company}</td> */}
              <td>{e.category}</td>
              <td><button type="button" className="btn btn-lg btn-danger" data-bs-toggle="popover" title="Popover title" data-bs-content="And here's some amazing content. It's very engaging. Right?">Click to toggle popover</button></td>
              <td><Button variant='contained' color='info' startIcon={<RemoveRedEye/>} onClick={toggleModal}>View</Button></td>
              <td><Button variant='contained' color="primary" startIcon={<Edit/>}>Edit</Button></td>
              <td><Button variant='contained' value={e.id} 
              onClick= {() => deleteProduct(e.id)}
              color="error" startIcon={<Delete/>} >Delete</Button></td>
              </tr>)}
          </tbody>
          </table>
          </div>
          </center>
          <Modal isOpen={modal} toggle={toggleModal}>
        <ModalBody>
            <section class="vh-50">
              <div class="mask d-flex align-items-center h-100 gradient-custom-3">
                <div class="container h-100">
                  <div class="row d-flex justify-content-center align-items-center h-100">
                    <div class="col-12">
                      <div class="card" style={{borderRadius: "15px"}}>
                        <div class="card-body p-2">
                          <h2 class="text-uppercase text-center mb-5">Create an account</h2>

                          <form>

                            <div class="form-outline mb-4">
                              <input type="text" id="form3Example1cg" class="form-control form-control-lg" />
                              <label class="form-label" for="form3Example1cg">Your Name</label>
                            </div>

                            <div class="form-outline mb-4">
                              <input type="email" id="form3Example3cg" class="form-control form-control-lg" />
                              <label class="form-label" for="form3Example3cg">Your Email</label>
                            </div>

                            <div class="form-outline mb-4">
                              <input type="password" id="form3Example4cg" class="form-control form-control-lg" />
                              <label class="form-label" for="form3Example4cg">Password</label>
                            </div>

                            <div class="form-outline mb-4">
                              <input type="password" id="form3Example4cdg" class="form-control form-control-lg" />
                              <label class="form-label" for="form3Example4cdg">Repeat your password</label>
                            </div>

                            <div class="form-check d-flex justify-content-center mb-5">
                              <input class="form-check-input me-2" type="checkbox" value="" id="form2Example3cg" />
                              <label class="form-check-label" for="form2Example3g">
                                I agree all statements in <a href="#!" class="text-body"><u>Terms of service</u></a>
                              </label>
                            </div>

                            <div class="d-flex justify-content-center">
                              <button type="button"
                                class="btn btn-success btn-block btn-lg gradient-custom-4 text-body">Register</button>
                            </div>

                            <p class="text-center text-muted mt-5 mb-0">Have already an account? <a href="#!"
                                class="fw-bold text-body"><u>Login here</u></a></p>

                          </form>

                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </section>
        </ModalBody>
        <ModalFooter>
          {/* <Button color="primary" onClick={toggleModal}>
            Do Something
          </Button>{' '} */}
          <Button color="secondary" onClick={toggleModal}>
            Cancel
          </Button>
        </ModalFooter>
  </Modal>

</>
    );
}

export default Adminproducts;
