import { Delete, Edit, RemoveRedEye } from "@mui/icons-material";
import { Button } from "@mui/material";
import axios from "axios";
import { useEffect, useState } from "react";
import { createUrl, log } from "../../utils/utils";
import { toast } from "react-toastify";
import { Modal, ModalBody, ModalFooter } from "reactstrap";

function Adminproducts() {
  const [products, setProducts] = useState([]);
  const [productName, setproductName] = useState([]);
  const [productPrice, setproductPrice] = useState([]);
  const [productUnitstock, setproductUnitstock] = useState([]);
  const [productDescription, setproductDescription] = useState([]);
  const [productCompany, setproductCompany] = useState([]);
  const [productCategory, setproductCategory] = useState([]);
  const [productImage, setProductImage] = useState(null);
  const [modal, setModal] = useState(false);
  const [tempid, settempid] = useState();

  const toggleModal = () => {
    setModal(!modal);
  };

  const wrappper=(id)=>{
    settempid(id);
    const t = tempid;
    // toggleModal();
    console.log("eid:"+id);
    console.log("check:"+t);
    console.log("check:"+tempid);
  }

  useEffect(() => {
    loadAllData();
  }, []);

  const loadAllData = async () => {
    try {
      const url = createUrl(`/admin/products/all`);
      const token = sessionStorage.getItem("jwt");
      const config = {
        headers: { Authorization: `Bearer ${token}` },
      };

      const response = await axios.get(url, config);
      setProducts(response.data);
    } catch (error) {
      console.error("Error fetching data: ", error);
    }
  };

  const addProduct = async () => {
    if (productName.length == "") {
      toast.error("Please enter Product name");
    } else if (productPrice.length == "") {
      toast.error("Please enter Product Price");
    } else if (productUnitstock.length == "") {
      toast.error("Please enter Product Unitstock");
    } else if (productDescription.length == "") {
      toast.error("Please enter Product Description");
    } else if (productCompany.length == "") {
      toast.error("Please enter Product Company");
    } else if (productCategory.length == "") {
      toast.error("Please enter Product Category");
    }
    const url = createUrl(`/admin/products`);
    const token = sessionStorage.getItem("jwt");

    const body = {
      productName,
      productPrice,
      productUnitstock,
      productDescription,
      productCompany,
      productCategory,
    };

    const formData = new FormData();
    formData.append("image", productImage); // Replace 'file' with the name you want to use for the file field
    formData.append("data", JSON.stringify(body)); // Replace 'jsonData' with the name you want to use for the JSON data field

    const config = {
      headers: {
        Authorization: `Bearer ${token}`,
        "Content-Type": "multipart/form-data",
      },
    };

    try {
      const response = await axios.post(url, formData, config);
      log(response.data);
      loadAllData();
      document.getElementById("updateVenue").hidden = true;
      document.getElementById("productTable").hidden = false;
      log(response);
      return response.data;
    } catch (error) {
      console.error("Error fetching data: ", error);
      return null;
    }
  };

  const update = () => {
    document.getElementById("updateVenue").hidden = true;
  };

  const deleteProduct = (e) => {
    try {
      const response = axios.delete(
        "http://localhost:8000/admin/deletevenue/${e}"
      );
    } catch (error) {
      console.error("Error fetching data: ", error);
      return null;
    }
  };

  return (
    <>
      <center>
        {/* <h1>
    Admin products Page
</h1> */}

        <div
          id="updateProduct"
          hidden="true"
          style={{ justifyContent: "center", textAlign: "center" }}
        >
          <h1 style={{ textAlign: "center", margin: 10 }}>Add Product</h1>

          <div className="row">
            <div className="col-12 d-flex justify-content-center">
              <div className="form">
                <div className="mb-3">
                  <label htmlFor="">Product Name</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={(e) => {
                      setproductName(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Price</label>
                  <input
                    type="number"
                    className="form-control"
                    onChange={(e) => {
                      setproductPrice(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Unit Stock</label>
                  <input
                    type="number"
                    className="form-control"
                    onChange={(e) => {
                      setproductUnitstock(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Product Description</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={(e) => {
                      setproductDescription(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Company</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={(e) => {
                      setproductCompany(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Category</label>
                  <input
                    type="text"
                    className="form-control"
                    onChange={(e) => {
                      setproductCategory(e.target.value);
                    }}
                  />
                </div>

                <div className="mb-3">
                  <label htmlFor="">Product Image</label>
                  <input
                    type="file"
                    className="form-control"
                    onChange={(e) => {
                      setProductImage(e.target.files[0]);
                    }}
                  />
                </div>
                <div style={{ display: "flex" }}>
                  <div className="mb-3">
                    <button
                      onClick={() => {
                        addProduct();
                      }}
                      className="btn btn-success"
                    >
                      Back
                    </button>
                  </div>
                  &nbsp;
                  <div className="mb-3">
                    <button
                      onClick={() => {
                        addProduct();
                      }}
                      className="btn btn-success"
                    >
                      Add product
                    </button>
                  </div>
                </div>
              </div>
            </div>
            <div className="col"></div>
          </div>
        </div>
        <div id="productTable">
          <div>
            <button
              type="button"
              className="btn btn-warning"
              style={{ margin: "10px" }}
              onClick={() => {
                document.getElementById("updateProduct").hidden = false;
                document.getElementById("productTable").hidden = true;
              }}
            >
              Add Products
            </button>
          </div>

          <table className="table table-hover">
            <thead>
              <tr>
                <th scope="col">id</th>
                <th scope="col" style={{ width: "200px" }}>
                  Product Name
                </th>
                <th scope="col">Price</th>
                <th scope="col">Unit Stock</th>
                {/* <th scope="col">Company</th> */}
                <th scope="col">Category</th>
                <th scope="col">View</th>
                <th scope="col">Edit</th>
                <th scope="col">Delete</th>
              </tr>
            </thead>
            <tbody>
              {products.map((a) => (
                <tr>
                  <th scope="row">{a.id}</th>
                  <td>{a.productName}</td>
                  <td>{a.price}</td>
                  <td>{a.unitStock}</td>
                  {/* <td>{e.company}</td> */}
                  <td>{a.category}</td>
                  <td>
                    <Button
                      style={{ height: 35, width: 75 }}
                      variant="contained"
                      value={a.id}
                      color="info"
                      startIcon={<RemoveRedEye />}
                      // onChange={(e) => {
                      //   debugger;
                      //   settempid(e.target.value);
                      //   debugger;
                      // }}
                      onClick={(q)=>wrappper(q.target.value)}
                    >
                      View
                    </Button>
                  </td>
                  <td>
                    <Button
                      style={{ height: 35, width: 75 }}
                      variant="contained"
                      color="primary"
                      startIcon={<Edit />}
                    >
                      Edit
                    </Button>
                  </td>
                  <td>
                    <Button
                      style={{ height: 35, width: 90 }}
                      variant="contained"
                      onClick={(e) => deleteProduct(e.id)}
                      color="error"
                      startIcon={<Delete />}
                    >
                      Delete
                    </Button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </center>

      <Modal isOpen={modal} toggle={toggleModal}>
      {products.filter((tempid)=>tempid==products.id).map((a) => (
        <>
          <ModalBody>
            <div className="card">
              <div className="card-header">Product Details</div>
              <div className="card-body">
                <h5 className="card-title">{a.id}</h5>
                <p className="card-text">{a.productDescription}</p>
                <a href="#" className="btn btn-primary">
                  Back
                </a>
              </div>
            </div>
          </ModalBody>
          <ModalFooter>
           
          </ModalFooter>
        </>
 
      )
   )
   } 
</Modal>
    </>
  );
}

export default Adminproducts;
