import { searchSlice } from '@reduxjs/toolkit'

export const persistedSearchReducer = searchSlice({
  // name of slice (must be unique)
  name: 'search',
  initialState: {
    itemSearched: [],
  },
  reducers: {
    // action: action handler
    addToSearch: (state,action) => {
      state.itemSearched = action.payload.quantity;
    },
    // action: action handler
    removeFromCart: (state,action) => {
      state.itemSearched = action.payload.quantity;
    },
    // cartCountSetter: (state) => {
    //   state.itemCounter = state
    // },
  },
})

export const { addToSearch, removeFromCart } = persistedSearchReducer.actions
export default persistedSearchReducer.reducer
