import { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify'
import { login, logout } from '../features/authSlice'
import { loginUser as loginUserApi } from '../services/user'
import backgroundImageUrl from '../components/images/back1.jpg'

function LoginUser() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  // const backgroundImageUrl = 'url(../components/images/back1.jpg)';

  // get the navigation object
  const navigate = useNavigate()

  // get dispatcher object
  const dispatch = useDispatch()

// useEffect=(()=>{
//   // sessionStorage.removeItem("jwt");
//   // sessionStorage.removeItem("email");
//   // sessionStorage.removeItem("first_name");
//   // sessionStorage.removeItem("id");
//   // dispatch(logout());
//   // navigate("/");
// },[])

  const loginUser = async () => {
    if (email.length == '') {
      toast.error('Please enter email')
    } else if (password.length == '') {
      toast.error('Please enter password')
    } else {
      const response = await loginUserApi(email, password)
      log("CHECKING"); 
      log(response);
        if (response['jwt'].length > 0) {

        const {first_name,email,id,jwt }= response

        sessionStorage['first_name'] = first_name
        sessionStorage['email'] = email
        sessionStorage['id'] = id
        sessionStorage['jwt'] = jwt

        dispatch(login())

        toast.success(`Welcome ${first_name} to store application`)

        navigate('/product-gallery')
      } else {
        toast.error('Invalid user name or password')
      }
    }
  }

  return (
    <div style={{height:"100%" ,backgroundImage:'url("https://img.freepik.com/free-photo/abstract-luxury-plain-blur-grey-black-gradient-used-as-background-studio-wall-display-your-products_1258-63622.jpg?w=1380&t=st=1693389246~exp=1693389846~hmac=17abaa008eda85e2c7df78f9ba407eb87c4ab8fb09c5019fbfdba3f4d4881a48")'}}>
    <div >
      <h1 style={{ textAlign: 'center', margin: 10}}>Login</h1>

      <div className='row'>
        <div className='col'></div>
        <div className='col'>
          <div className='form'>
            <div className='mb-3'>
              <label htmlFor=''>Email</label>
              <input
                type='text'
                className='form-control'
                onChange={(e) => {
                  setEmail(e.target.value)
                }}
              />
            </div>
            <div className='mb-3'>
              <label htmlFor=''>Password</label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => {
                  setPassword(e.target.value)
                }}
              />
            </div>
            <div className='mb-3'>
              <div className='mb-3'>
                Don't have an account? <Link to='/register'>Register here</Link>
              </div>
              <button onClick={loginUser} className='btn btn-success'>
                Login
              </button>
            </div>
          </div>
        </div>
        <div className='col'></div>
      </div>
    </div>
    </div>
  )
}

export default LoginUser
