import { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux'
import { Link, useNavigate } from 'react-router-dom'
import { toast } from 'react-toastify'
import { login, logout } from '../features/authSlice'
import { loginUser as loginUserApi } from '../services/user'
import backgroundImageUrl from '../components/images/back1.jpg'

function LoginUser() {
  const [email, setEmail] = useState('')
  const [password, setPassword] = useState('')
  // const backgroundImageUrl = 'url(../components/images/back1.jpg)';

  // get the navigation object
  const navigate = useNavigate()

  // get dispatcher object
  const dispatch = useDispatch()

// useEffect=(()=>{
//   // sessionStorage.removeItem("jwt");
//   // sessionStorage.removeItem("email");
//   // sessionStorage.removeItem("first_name");
//   // sessionStorage.removeItem("id");
//   // dispatch(logout());
//   // navigate("/");
// },[])

  const loginUser = async () => {
    if (email.length == '') {
      toast.error('Please enter email')
    } else if (password.length == '') {
      toast.error('Please enter password')
    } else {
      // call register api
      const response = await loginUserApi(email, password)
      console.log("data1 : "+ response);
      // parse the response
      // if (response['status'] === 'success') {
        if (response['jwt'].length > 0) {
          console.log("check")
        // parse the response's data and extract the token
        // const { token, name, mobile, profileImage } = response['data']
        console.log("data : "+response);
        const {first_name,email,id,jwt }= response
        // const  jwt = response['data']
        // store the token for making other apis
        // sessionStorage['token'] = token
        // sessionStorage['name'] = name
        // sessionStorage['mobile'] = mobile
        // sessionStorage['profileImage'] = profileImage

        sessionStorage['first_name'] = first_name
        sessionStorage['email'] = email
        sessionStorage['id'] = id
        sessionStorage['jwt'] = jwt

        // update global store's authSlice with status = true
        dispatch(login())

        toast.success(`Welcome ${first_name} to store application`)

        // go back to login
        navigate('/product-gallery')
      } else {
        toast.error('Invalid user name or password')
      }
    }
  }

  return (
    <div style={{height:"100%" ,backgroundImage:'url("https://img.freepik.com/free-photo/abstract-grunge-decorative-relief-navy-blue-stucco-wall-texture-wide-angle-rough-colored-background_1258-28311.jpg?w=1380&t=st=1693389090~exp=1693389690~hmac=cc1249e9d678492f08bb104d52ba26ea3692d0c6c50f8205414aa1a935f3fd8d")'}}>
    <div >
      <h1 style={{ textAlign: 'center', margin: 10}}>Login</h1>

      <div className='row'>
        <div className='col'></div>
        <div className='col'>
          <div className='form'>
            <div className='mb-3'>
              <label htmlFor=''>Email</label>
              <input
                type='text'
                className='form-control'
                onChange={(e) => {
                  setEmail(e.target.value)
                }}
              />
            </div>
            <div className='mb-3'>
              <label htmlFor=''>Password</label>
              <input
                type='password'
                className='form-control'
                onChange={(e) => {
                  setPassword(e.target.value)
                }}
              />
            </div>
            <div className='mb-3'>
              <div className='mb-3'>
                Don't have an account? <Link to='/register'>Register here</Link>
              </div>
              <button onClick={loginUser} className='btn btn-success'>
                Login
              </button>
            </div>
          </div>
        </div>
        <div className='col'></div>
      </div>
    </div>
    </div>
  )
}

export default LoginUser
